    <div className="subtotal">
      {/* Price */}
      <CurrencyFormat
        rendertext={(value) => (
          <>
            <p>
              Subtotal ({basket.length} items): <strong>{``}</strong>
            </p>
            <small className="subtotal__gift">
              <input type="checkbox" />
              This order contains
            </small>
          </>
        )}
        decimalScale={2}
        value={0}
        displayType={"text"}
        thousandSeparator={true}
